CREATE PROCEDURE [dbo].[MakeUser]
	@Email nvarchar(50)
AS

(
	SELECT ID, FirstName, LastName, Email, DisplayName, PhoneNumber, Address, City, Country, Permission FROM UserInfoSearch WHERE SearchString LIKE CONCAT('%',@SearchTerm,'%')
)
